import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  Alert,
  ImageBackground,
  Image,
} from "react-native";
import { Camera } from "expo-camera";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useEffect } from "react";
import moment from "moment";
import firebase from "../../firebase/config";
import { db, storage } from "../../firebase/config";
import { useNavigation } from "@react-navigation/native";
import { useDispatch, useSelector } from "react-redux";
import { setLoading } from "../../redux/public/actions";
import { useForm, Controller } from "react-hook-form";
import { useTheme, TextInput, Button } from "react-native-paper";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from "react-native-responsive-screen";
import Toast from "react-native-toast-message";
import { CarouselComponent } from "../../components/carousel";

let camera;
export default function ReportMaintenanceScreen() {
  const { colors } = useTheme();
  const [startCamera, setStartCamera] = useState(false);
  const [previewVisible, setPreviewVisible] = useState(false);
  const [capturedImage, setCapturedImage] = useState(null);
  const [cameraType, setCameraType] = useState(Camera.Constants.Type.back);
  const [flashMode, setFlashMode] = useState("off");
  const { user } = useSelector((state) => state.public);
  const [description, setDescription] = useState("");
  const [subject, setSubject] = useState("");
  const [photos, setPhotos] = useState([]);
  const navigation = useNavigation();
  const dispatch = useDispatch();

  const __startCamera = async () => {
    const { status } = await Camera.requestPermissionsAsync();
    console.log(status);
    if (status === "granted") {
      setStartCamera(true);
    } else {
      Alert.alert("Access denied");
    }
  };
  const __takePicture = async () => {
    const photo = await camera.takePictureAsync();
    setPreviewVisible(true);
    //setStartCamera(false)
    setCapturedImage(photo);
  };

  const __retakePicture = () => {
    setCapturedImage(null);
    setPreviewVisible(false);
    __startCamera();
  };
  const __handleFlashMode = () => {
    if (flashMode === "on") {
      setFlashMode("off");
    } else if (flashMode === "off") {
      setFlashMode("on");
    } else {
      setFlashMode("auto");
    }
  };
  const __switchCamera = () => {
    if (cameraType === "back") {
      setCameraType("front");
    } else {
      setCameraType("back");
    }
  };

  const __savePhoto = async () => {
    setPreviewVisible(false);
    setStartCamera(false);
    let _photos = [...photos];
    _photos.push(capturedImage);
    console.log(_photos);
    setPhotos(_photos);
  };

  const onSubmit = async (values) => {
    console.log(values);
    dispatch(setLoading(true));

    try {
      let _downdloadUrls = [];
      const date = new Date();
      const filename = `${date.getDate()}_${date.getMonth()}_${date.getFullYear()}_${date.getHours()}_${date.getMinutes()}_${date.getSeconds()}`;

      photos.forEach((item, index) => {
        fetch(item.uri)
          .then((res) => res.blob())
          .then((blob) => {
            const file = new File([blob], "File name", { type: "image/png" });
            const uploadTask = storage
              .ref()
              .child(`/maintenance/${user.invitedBy}/${filename}_${index}`)
              .put(file);

            uploadTask.on(
              firebase.storage.TaskEvent.STATE_CHANGED,
              (snapshot) => {
                const progress =
                  (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                console.log(`Progress: ${progress}%`);
                if (snapshot.state === firebase.storage.TaskState.RUNNING) {
                  console.log("file uploading...");
                }
                // ...etc
              },
              (error) => console.log(error.code),
              async () => {
                const downloadURL = await uploadTask.snapshot.ref.getDownloadURL();
                _downdloadUrls.push(downloadURL);
                if (index === photos.length - 1) {
                  const _values = {
                    condoId: user.invitedBy.trim(),
                    reporterUid: user.uid,
                    reporterName: `${user.firstName} ${user.lastName}`,
                    subject: subject,
                    description: description,
                    image: _downdloadUrls,
                    date: new Date(),
                    status: "OPEN",
                  };

                  console.log(_values);

                  db.collection("maintenance")
                    .add(_values)
                    .then((docRef) => {
                      setStartCamera(false);
                      setCapturedImage(null);
                      setPhotos([]);
                      setDescription("");
                      Toast.show({
                        position: "bottom",
                        text1: "Report successfully submitted. ",
                      });
                      dispatch(setLoading(false));
                    });
                }
              }
            );
          });
      });
    } catch (e) {
      dispatch(setLoading(false));
      console.log(e);
    }
  };

  return (
    <View style={styles.container}>
      {startCamera ? (
        <View
          style={{
            flex: 1,
            width: "100%",
            height: "100%",
          }}
        >
          {previewVisible && capturedImage ? (
            <CameraPreview
              photo={capturedImage}
              savePhoto={__savePhoto}
              retakePicture={__retakePicture}
            />
          ) : (
            <Camera
              type={cameraType}
              flashMode={flashMode}
              style={{ flex: 1 }}
              ref={(r) => {
                camera = r;
              }}
            >
              <View
                style={{
                  flex: 1,
                  width: "100%",
                  height: "100%",
                  backgroundColor: "transparent",
                  flexDirection: "row",
                }}
              >
                <View
                  style={{
                    position: "absolute",
                    left: "5%",
                    top: "10%",
                    flexDirection: "column",
                    justifyContent: "space-between",
                  }}
                >
                  <TouchableOpacity
                    onPress={__handleFlashMode}
                    style={{
                      backgroundColor: flashMode === "off" ? "#000" : "#fff",
                      borderRadius: "50%",
                      height: 25,
                      width: 25,
                    }}
                  >
                    <Text
                      style={{
                        fontSize: 20,
                      }}
                    >
                      ‚ö°Ô∏è
                    </Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    onPress={__switchCamera}
                    style={{
                      marginTop: 20,
                      borderRadius: "50%",
                      height: 25,
                      width: 25,
                    }}
                  >
                    <Text
                      style={{
                        fontSize: 20,
                      }}
                    >
                      {cameraType === "front" ? "ü§≥" : "üì∑"}
                    </Text>
                  </TouchableOpacity>
                </View>
                <View
                  style={{
                    position: "absolute",
                    bottom: 0,
                    flexDirection: "row",
                    flex: 1,
                    width: "100%",
                    padding: 20,
                    justifyContent: "space-between",
                  }}
                >
                  <View
                    style={{
                      alignSelf: "center",
                      flex: 1,
                      alignItems: "center",
                    }}
                  >
                    <TouchableOpacity
                      onPress={__takePicture}
                      style={{
                        width: 70,
                        height: 70,
                        bottom: 0,
                        borderRadius: 50,
                        backgroundColor: "#ffffff",
                      }}
                    />
                  </View>
                </View>
              </View>
            </Camera>
          )}
        </View>
      ) : (
        <View
          style={{
            flex: 1,
            alignItems: "center",
          }}
        >
          <TextInput
            autoCapitalize="none"
            name="Subject"
            mode="outlined"
            label="Subject"
            style={{
              width: wp("95%"),
              marginBottom: 5,
              padding: 10,
              fontSize: 20,
            }}
            onChangeText={(value) => setSubject(value)}
            value={subject}
          />

          <TextInput
            autoCapitalize="none"
            multiline={true}
            name="Description"
            mode="outlined"
            label="Description"
            numberOfLines={4}
            style={{
              height: hp("10%"),
              width: wp("95%"),
              marginBottom: 5,
              padding: 10,
              fontSize: 20,
            }}
            onChangeText={(value) => setDescription(value)}
            value={description}
          />

          <TouchableOpacity
            onPress={__startCamera}
            style={{
              width: 130,
              borderRadius: 4,
              backgroundColor: colors.primary,
              flexDirection: "row",
              justifyContent: "center",
              alignItems: "center",
              height: 40,
            }}
          >
            <Text
              style={{
                color: "#fff",
                fontWeight: "bold",
                textAlign: "center",
              }}
            >
              Take picture
            </Text>
          </TouchableOpacity>
        </View>
      )}
      {photos.length !== 0 && !previewVisible && !startCamera && (
        <>
          <View
            style={{
              backgroundColor: "transparent",
              width: "100%",
              height: hp("40%"),
              justifyContent: "center",
              alignContent: "center",
              marginTop: 30,
              alignItems: "center",
            }}
          >
            <CarouselComponent photos={photos} />
          </View>
          {description && subject ? (
            photos.length !== 0 ? (
              <Button
                mode="outline"
                style={{
                  width: 300,
                  marginBottom: 10,
                  padding: 10,
                  backgroundColor: colors.primary,
                }}
                onPress={onSubmit}
              >
                <Text style={{ color: "#ffffff" }}>SUBMIT</Text>
              </Button>
            ) : null
          ) : null}
        </>
      )}

      <StatusBar style="auto" />
    </View>
  );
}

