import React, { FC, useEffect, useState } from "react";
import {
  Platform,
  StyleSheet,
  Text,
  View,
  ScrollView,
  Dimensions,
  Modal,
  Pressable,
  TouchableOpacity,
  Image,
  ImageBackground,
} from "react-native";
import firebase from "../../../firebase/config";
import Toast from "react-native-toast-message";
import { useSelector, useDispatch } from "react-redux";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from "react-native-responsive-screen";
import { db, storage } from "../../../firebase/config";
import moment from "moment";
import { Button, useTheme } from "react-native-paper";
import { CarouselComponent } from "../../../components/carousel";
import RNPickerSelect, { defaultStyles } from "react-native-picker-select";
import { theme } from "../../../theme";
import { Camera } from "expo-camera";
import { setLoading } from "../../../redux/public/actions";
let camera;

export default function ReportsDetailsScreen({ navigation, route }) {
  const [startCamera, setStartCamera] = useState(false);
  const [previewVisible, setPreviewVisible] = useState(false);
  const [capturedImage, setCapturedImage] = useState(null);
  const [cameraType, setCameraType] = useState(Camera.Constants.Type.back);
  const [flashMode, setFlashMode] = useState("off");
  const dispatch = useDispatch();
  const { ticket } = route.params;
  const { user } = useSelector((state) => state.public);
  const [tickets, setTickets] = useState([]);
  const { colors } = useTheme();
  const [modalVisible, setModalVisible] = useState(false);
  const [image, setImage] = useState("");
  const reportsRef = db.collection("maintenance");
  const engRef = db.collection("users");
  const [isHead, setIsHead] = useState(false);
  const [engEmployee, setEngEmployee] = useState([]);
  const [assignee, setAssignee] = useState(null);
  const [reports, setReports] = useState([]);
  const [photos, setPhotos] = useState([]);

  const __startCamera = async () => {
    const { status } = await Camera.requestPermissionsAsync();
    console.log(status);
    if (status === "granted") {
      setStartCamera(true);
    } else {
      Alert.alert("Access denied");
    }
  };
  const __takePicture = async () => {
    const photo = await camera.takePictureAsync();
    setPreviewVisible(true);
    setCapturedImage(photo);
  };

  const __retakePicture = () => {
    setCapturedImage(null);
    setPreviewVisible(false);
    __startCamera();
  };
  const __handleFlashMode = () => {
    if (flashMode === "on") {
      setFlashMode("off");
    } else if (flashMode === "off") {
      setFlashMode("on");
    } else {
      setFlashMode("auto");
    }
  };
  const __switchCamera = () => {
    if (cameraType === "back") {
      setCameraType("front");
    } else {
      setCameraType("back");
    }
  };

  const __savePhoto = async () => {
    let _photos = [];
    _photos.push(capturedImage);
    setPreviewVisible(false);
    setStartCamera(false);
    setPhotos(_photos);
  };

  const getEngineeringEmployees = () => {
    const unsubscribe = engRef
      .where("userType", "==", "EMPLOYEE")
      .where("accessModules", "array-contains", "ENGINEERING")
      .onSnapshot((snapshot) => {
        let _engEmployees = [];
        snapshot.docs.forEach((doc) => {
          _engEmployees.push({
            value: doc.data().uid,
            label: `${doc.data().firstName} ${doc.data().lastName}`,
          });
        });
        setEngEmployee(_engEmployees);
      });
    return unsubscribe;
  };

  const getTickets = async () => {
    if (user.accessModules.includes("ENGINEERING_HEAD")) {
      setIsHead(true);
      const unsubscribe = reportsRef
        .where("condoId", "==", user?.invitedBy)
        .where("status", "not-in", ["OPEN", "REJECT"])
        .onSnapshot((snapshot) => {
          let _reports = [];
          snapshot.docs.forEach((doc) => {
            let _doc = {
              ...doc.data(),
              id: doc.id,
            };
            _reports.push(_doc);
          });
          setReports(_reports);
        });

      return unsubscribe;
    } else {
      setIsHead(false);
      const unsubscribe = reportsRef
        .where("condoId", "==", user?.invitedBy)
        .where("assignee.id", "==", user?.uid)
        .onSnapshot((snapshot) => {
          console.log(snapshot.docs);
          let _reports = [];
          snapshot.docs.forEach((doc) => {
            let _doc = {
              ...doc.data(),
              id: doc.id,
            };
            _reports.push(_doc);
          });
          console.log(_reports);
          setReports(_reports);
        });

      return unsubscribe;
    }
  };

  const handleAssign = async (id) => {
    let emp = engEmployee.filter((item) => item.value === assignee);

    await reportsRef.doc(id).update({
      status: "ASSIGNED",
      assignee: {
        id: assignee,
        name: emp[0].label,
      },
    });
    Toast.show({
      position: "bottom",
      text1: "Assignee Updated.",
    });
  };

  const submitForApproval = async (id) => {
    const date = new Date();
    const filename = `${date.getDate()}_${date.getMonth()}_${date.getFullYear()}_${date.getHours()}_${date.getMinutes()}_${date.getSeconds()}`;

    if (photos.length) {
      photos.forEach((item, index) => {
        fetch(item.uri)
          .then((res) => res.blob())
          .then((blob) => {
            const file = new File([blob], "File name", { type: "image/png" });
            const uploadTask = storage
              .ref()
              .child(`/maintenance/${user.invitedBy}/${filename}_${index}`)
              .put(file);

            uploadTask.on(
              firebase.storage.TaskEvent.STATE_CHANGED,
              (snapshot) => {
                const progress =
                  (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                console.log(`Progress: ${progress}%`);
                if (snapshot.state === firebase.storage.TaskState.RUNNING) {
                  console.log("file uploading...");
                }
                // ...etc
              },
              (error) => console.log(error.code),
              async () => {
                const downloadURL = await uploadTask.snapshot.ref.getDownloadURL();
                if (index === photos.length - 1) {
                  await reportsRef.doc(id).update({
                    status: "SUBMITTED",
                    imageSubmitted: downloadURL,
                    dateAssigneeSubmitted: new Date(),
                  });

                  Toast.show({
                    position: "bottom",
                    text1: "Report successfully submitted. ",
                  });
                  dispatch(setLoading(false));
                  navigation.goBack();
                }
              }
            );
          });
      });
    }
  };

  const returnToAssignee = () => {
    if (assignee) {
      reportsRef.doc(ticket.id).update({
        status: "ASSIGNED",
      });
      Toast.show({
        position: "bottom",
        text1: `Ticket returned to assignee.`,
      });
    }
  };

  const setAsDone = () => {
    if (assignee) {
      reportsRef.doc(ticket.id).update({
        status: "DONE",
        dateDone: new Date(),
      });
      if (ticket?.asset?.id) {
        assetsRef.doc(ticket.asset.id).collection("history").add({
          assetName: ticket.asset.assetName,
          assetCode: ticket.asset.assetCode,
          date: new Date(),
          type: "ASSET_REPORTS",
          others: "ITEM DONE FIXING",
        });
        Toast.show({
          position: "bottom",
          text1: `Ticket successfully set as done.`,
        });
      }
    }
  };

  const pulloutAsset = () => {
    reportsRef.doc(ticket.id).update({
      status: "PULLED-OUT",
    });
    if (ticket?.asset?.id) {
      assetsRef.doc(ticket.asset.id).collection("history").add({
        assetName: ticket.asset.assetName,
        assetCode: ticket.asset.assetCode,
        date: new Date(),
        location: "PULLED-OUT",
        type: "ASSET_REPORTS",
        others: "ITEM PULLED OUT",
      });
      Toast.show({
        position: "bottom",
        text1: `Asset tagged as Pull Out.`,
      });
    }
  };

  useEffect(() => {
    getEngineeringEmployees();
    getTickets();
  }, []);

  return (
    <>
      {startCamera ? (
        <View
          style={{
            flex: 1,
            width: "100%",
            height: "100%",
          }}
        >
          {previewVisible && capturedImage ? (
            <CameraPreview
              photo={capturedImage}
              savePhoto={__savePhoto}
              retakePicture={__retakePicture}
            />
          ) : (
            <Camera
              type={cameraType}
              flashMode={flashMode}
              style={{ flex: 1 }}
              ref={(r) => {
                camera = r;
              }}
            >
              <View
                style={{
                  flex: 1,
                  width: "100%",
                  height: "100%",
                  backgroundColor: "transparent",
                  flexDirection: "row",
                }}
              >
                <View
                  style={{
                    position: "absolute",
                    left: "5%",
                    top: "10%",
                    flexDirection: "column",
                    justifyContent: "space-between",
                  }}
                >
                  <TouchableOpacity onPress={() => setStartCamera(false)}>
                    <Text
                      style={{
                        color: "#ffffff",
                        fontSize: 20,
                        marginVertical: 10,
                      }}
                    >
                      BACK
                    </Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    onPress={__handleFlashMode}
                    style={{
                      backgroundColor: flashMode === "off" ? "#000" : "#fff",
                      borderRadius: 50,
                      height: 25,
                      width: 25,
                    }}
                  >
                    <Text
                      style={{
                        fontSize: 20,
                      }}
                    >
                      ⚡️
                    </Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    onPress={__switchCamera}
                    style={{
                      marginTop: 20,
                      borderRadius: 50,
                      height: 25,
                      width: 25,
                    }}
                  >
                    <Text
                      style={{
                        fontSize: 20,
                      }}
                    >
                      {cameraType === "front" ? "🤳" : "📷"}
                    </Text>
                  </TouchableOpacity>
                </View>
                <View
                  style={{
                    position: "absolute",
                    bottom: 0,
                    flexDirection: "row",
                    flex: 1,
                    width: "100%",
                    padding: 20,
                    justifyContent: "space-between",
                  }}
                >
                  <View
                    style={{
                      alignSelf: "center",
                      flex: 1,
                      alignItems: "center",
                    }}
                  >
                    <TouchableOpacity
                      onPress={__takePicture}
                      style={{
                        width: 70,
                        height: 70,
                        bottom: 0,
                        borderRadius: 50,
                        backgroundColor: "#ffffff",
                      }}
                    />
                  </View>
                </View>
              </View>
            </Camera>
          )}
        </View>
      ) : null}
