              </Text>

              {!isHead ? (
                <>
                  <TouchableOpacity
                    disabled={ticket?.status === "DONE" ? true : false}
                    style={{
                      backgroundColor:
                        ticket?.status === "DONE"
                          ? "#c1c1c1"
                          : theme.colors.accent,
                      padding: 10,
                      margin: 10,
                    }}
                    onPress={() => {
                      submitForApproval(ticket.id);
                    }}
                  >
                    <Text
                      style={{
                        color: "#ffffff",
                        fontSize: wp("4%"),
                        textAlign: "center",
                      }}
                    >
                      SUBMIT FOR APPROVAL
                    </Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    disabled={ticket?.status === "DONE" ? true : false}
                    style={{
                      backgroundColor:
                        ticket?.status === "DONE"
                          ? "#c1c1c1"
                          : theme.colors.accent,
                      padding: 10,
                      margin: 10,
                    }}
                    onPress={() => {
                      pulloutAsset();
                    }}
                  >
                    <Text
                      style={{
                        color: "#ffffff",
                        fontSize: wp("4%"),
                        textAlign: "center",
                      }}
                    >
                      PULL OUT ASSSET
                    </Text>
                  </TouchableOpacity>

                  <TouchableOpacity
                    disabled={ticket?.status === "DONE" ? true : false}
                    style={{
                      backgroundColor:
                        ticket?.status === "DONE"
                          ? "#c1c1c1"
                          : theme.colors.accent,
                      padding: 10,
                      margin: 10,
                    }}
                    onPress={__startCamera}
                  >
                    <Text
                      style={{
                        color: "#ffffff",
                        fontSize: wp("4%"),
                        textAlign: "center",
                      }}
                    >
                      UPLOAD AFTER FIX PHOTO
                    </Text>
                  </TouchableOpacity>
                </>
              ) : null}

              {isHead && ticket.status !== "DONE" ? (
                <View
                  style={{
                    marginVertical: 10,
                    justifyContet: "center",
                    alignItem: "center",
                    alignSelf: "center",
                    width: wp("80%"),
                  }}
                >
                  <RNPickerSelect
                    disabled={ticket?.status === "ASSIGNED" ? true : false}
                    placeholder={{
                      label: "Select Engineering Staff",
                      color: "#ffffff",
                    }}
                    items={engEmployee}
                    onValueChange={(value) => {
                      setAssignee(value);
                    }}
                    style={pickerSelectStyles}
                    value={assignee}
                    useNativeAndroidPickerStyle={false}
                  />
                  <TouchableOpacity
                    disabled={ticket?.status === "ASSIGNED" ? true : false}
                    style={{
                      backgroundColor:
                        ticket?.status === "ASSIGNED"
                          ? "#c1c1c1"
                          : theme.colors.accent,
                      padding: 10,
                      marginVertical: 10,
                    }}
                    onPress={() => {
                      handleAssign(ticket.id);
                    }}
                  >
                    <Text style={{ color: "#ffffff", fontSize: wp("4%") }}>
                      ASSIGN
                    </Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={{
                      backgroundColor: "green",
                      padding: 10,
                      marginVertical: 10,
                    }}
                    onPress={() => {
                      setAsDone();
                    }}
                  >
                    <Text style={{ color: "#ffffff", fontSize: wp("4%") }}>
                      SET AS DONE
                    </Text>
                  </TouchableOpacity>
                  <TouchableOpacity
                    style={{
                      backgroundColor: "red",
                      padding: 10,
                      marginVertical: 10,
                    }}
                    onPress={() => {
                      returnToAssignee();
                    }}
                  >
                    <Text style={{ color: "#ffffff", fontSize: wp("4%") }}>
                      RETURN TO ASSIGNEE
                    </Text>
                  </TouchableOpacity>
                </View>
              ) : null}
            </View>
            <TouchableOpacity
              style={[
                {
                  backgroundColor: colors.primary,
                  position: "absolute",
                  top: 15,
                  right: 20,
                  padding: 7,
                  borderRadius: 3,
                },
              ]}
              onPress={() => {
                setImage(ticket?.image);
                setModalVisible(true);
              }}
            >
              <Text style={{ color: "#ffffff", fontSize: wp("3%") }}>
                View Image
              </Text>
            </TouchableOpacity>
          </View>
        </ScrollView>
      </View>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          setModalVisible(!modalVisible);
        }}
      >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <CarouselComponent photos={image} />
            <Pressable
              style={[styles.btn, styles.buttonClose]}
              onPress={() => setModalVisible(!modalVisible)}
            >
              <Text style={styles.textStyle}>Close</Text>
            </Pressable>
          </View>
        </View>
      </Modal>
    </>
  );
}

