import React, { FC, useEffect, useState } from "react";
import { Platform, StyleSheet, Text, View } from "react-native";
import { useNavigation, useIsFocused } from "@react-navigation/native";
import { TextInput, Button } from "react-native-paper";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useSelector, useDispatch } from "react-redux";
import Toast from "react-native-toast-message";
import { setLoading, setUser } from "../../redux/public/actions";
import { theme } from "../../theme";
import {
  heightPercentageToDP as hp,
  widthPercentageToDP as wp,
} from "react-native-responsive-screen";
import { Avatar } from "react-native-paper";
import { db } from "../../firebase/config";

export default function HomeScreen() {
  const dispatch = useDispatch();
  const navigation = useNavigation();
  const { user } = useSelector((state) => state.public);
  const isFocus = useIsFocused();
  const [condoInfo, setCondoInfo] = useState();

  const checkUser = async () => {
    dispatch(setLoading(true));
    try {
      const user = await AsyncStorage.getItem("@user");
      const _user = JSON.parse(user);
      if (!_user) {
        dispatch(setLoading(false));
        navigation.navigate("Auth");
        return;
      }
      dispatch(setUser(_user));
      dispatch(setLoading(false));
    } catch (e) {
      dispatch(setUser(null));
      navigation.navigate("Auth");
      console.log(e);
    }
  };

  useEffect(() => {
    checkUser();
  }, []);

  // Get Condo Information
  const getCondoInfo = () => {
    if (!user) {
      return;
    }

    const condoRef = db.collection("condos").doc(user.invitedBy);

    const unsubscribed = condoRef.onSnapshot((doc) => {
      setCondoInfo(doc.data());
    });
    return unsubscribed;
  };

  useEffect(() => {
    getCondoInfo();
  }, [isFocus]);

  return (
    <View style={styles.container}>
      <View
        style={{
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Text
          style={{
            marginBottom: 10,
            fontSize: wp("6%"),
            textAlign: "center",
          }}
        >
          {" "}
          Welcome
        </Text>
        {condoInfo?.logo && (
          <Avatar.Image size={100} source={{ uri: condoInfo?.logo }} />
        )}
        <Text
          style={{
            marginVertical: 20,
            fontSize: wp("4%"),
            textAlign: "center",
          }}
        >
          {condoInfo?.name}. {"\n"} Hello there, {user?.greetName}
        </Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  name: {
    fontSize: 20,
    color: theme.colors.primary,
  },
});
