import React, { FC, useEffect, useState } from "react";
import {
  Platform,
  StyleSheet,
  Text,
  View,
  ScrollView,
  Dimensions,
  Modal,
  Pressable,
  TouchableOpacity,
  Image,
} from "react-native";
import { useNavigation } from "@react-navigation/native";
import { useSelector, useDispatch } from "react-redux";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from "react-native-responsive-screen";
import Toast from "react-native-toast-message";
import { setLoading, setUser } from "../../../redux/public/actions";
import { db } from "../../../firebase/config";
import moment from "moment";
import { Button, useTheme } from "react-native-paper";
import { ImageComponent } from "../../../components/image";
import { CarouselComponent } from "../../../components/carousel";
import RNPickerSelect, { defaultStyles } from "react-native-picker-select";

export default function ReportsScreen() {
  const dispatch = useDispatch();
  const navigation = useNavigation();
  const { user } = useSelector((state) => state.public);
  const [tickets, setTickets] = useState([]);
  const { colors } = useTheme();
  const [modalVisible, setModalVisible] = useState(false);
  const [image, setImage] = useState("");
  const reportsRef = db.collection("maintenance");
  const engRef = db.collection("users");
  const [isHead, setIsHead] = useState(false);
  const [engEmployee, setEngEmployee] = useState([]);
  const [assignee, setAssignee] = useState(null);
  const [reports, setReports] = useState([]);

  const getEngineeringEmployees = () => {
    const unsubscribe = engRef
      .where("userType", "==", "EMPLOYEE")
      .where("accessModules", "array-contains", "ENGINEERING")
      .onSnapshot((snapshot) => {
        let _engEmployees = [];
        snapshot.docs.forEach((doc) => {
          _engEmployees.push({
            value: doc.data().uid,
            label: `${doc.data().firstName} ${doc.data().lastName}`,
          });
        });
        setEngEmployee(_engEmployees);
      });
    return unsubscribe;
  };

  const getTickets = async () => {
    if (user.accessModules.includes("ENGINEERING_HEAD")) {
      setIsHead(true);
      const unsubscribe = reportsRef
        .where("condoId", "==", user?.invitedBy)
        .where("status", "not-in", ["OPEN", "REJECT"])
        .onSnapshot((snapshot) => {
          let _reports = [];
          snapshot.docs.forEach((doc) => {
            let _doc = {
              ...doc.data(),
              id: doc.id,
            };
            _reports.push(_doc);
          });
          setReports(_reports);
        });

      return unsubscribe;
    } else {
      setIsHead(false);
      const unsubscribe = reportsRef
        .where("condoId", "==", user?.invitedBy)
        .where("assignee.id", "==", user?.uid)
        .onSnapshot((snapshot) => {
          console.log(snapshot.docs);
          let _reports = [];
          snapshot.docs.forEach((doc) => {
            let _doc = {
              ...doc.data(),
              id: doc.id,
            };
            _reports.push(_doc);
          });
          console.log(_reports);
          setReports(_reports);
        });

      return unsubscribe;
    }
  };

  // const handleChange = (e: any, id: any) => {
  //   setAssignee(e.target.value);
  //   reportsRef.doc(id).update({
  //     status: "ASSIGNED",
  //     assignee: {
  //       id: e.target.value.uid,
  //       name: `${e.target.value.firstName} ${e.target.value.lastName}`,
  //     },
  //   });
  // };

  const setAsDone = (id) => {
    reportsRef.doc(id).update({
      status: "DONE",
    });
  };

  useEffect(() => {
    getEngineeringEmployees();
    getTickets();
  }, []);

  return (
    <>
      <View style={styles.container}>
        <Text style={styles.name}>All Tickets</Text>
        <ScrollView
          contentContainerStyle={styles.scrollContainer}
          showsHorizontalScrollIndicator={false}
        >
          {reports.map((item, index) => {
            return (
              <View key={index}>
                <View style={styles.card}>
                  <Text style={styles.text}>Subject: {item?.subject}</Text>
                  <Text style={styles.text}>
                    Description: {item?.description}
                  </Text>
                  <Text style={styles.text}>
                    Date:{" "}
                    {moment(item?.date.toDate()).format("MMM DD YYYY h:mm A")}
                  </Text>
                  <Text style={styles.text}>Status: {item?.status}</Text>
                </View>
                {!isHead ? (
                  <TouchableOpacity
                    style={[styles.btnOptions, { backgroundColor: "green" }]}
                    onPress={() => {
                      setAsDone(item.id);
                    }}
                  >
                    <Text style={{ color: "#ffffff", fontSize: wp("4%") }}>
                      DONE
                    </Text>
                  </TouchableOpacity>
                ) : null}
                {isHead && item.status !== "DONE" ? (
                  <View
                    style={{ justifyContet: "center", alignItem: "center" }}
                  >
                    <RNPickerSelect
                      placeholder={"Select Engineering Staff"}
                      items={engEmployee}
                      onValueChange={(value) => {
                        setAssignee(value);
                      }}
                      style={pickerSelectStyles}
                      value={assignee}
                      useNativeAndroidPickerStyle={false}
                    />
                    <TouchableOpacity
                      style={[styles.btnOptions, { backgroundColor: "green" }]}
                      onPress={() => {
                        changeStatusApprove(item.id);
                      }}
                    >
                      <Text style={{ color: "#ffffff", fontSize: wp("4%") }}>
                        ASSIGN
                      </Text>
                    </TouchableOpacity>
                  </View>
                ) : null}

                <TouchableOpacity
                  style={[styles.btn, { backgroundColor: colors.primary }]}
                  onPress={() => {
                    setImage(item?.image);
                    setModalVisible(true);
                  }}
                >
                  <Text style={{ color: "#ffffff", fontSize: wp("4%") }}>
                    View Image
                  </Text>
                </TouchableOpacity>
              </View>
            );
          })}
        </ScrollView>
      </View>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          setModalVisible(!modalVisible);
        }}
      >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <CarouselComponent photos={image} />
            <Pressable
              style={[styles.btn, styles.buttonClose]}
              onPress={() => setModalVisible(!modalVisible)}
            >
              <Text style={styles.textStyle}>Close</Text>
            </Pressable>
          </View>
        </View>
      </Modal>
    </>
  );
}

const width = Dimensions.get("window").width;

const pickerSelectStyles = StyleSheet.create({
  inputIOS: {
    fontSize: 16,
    paddingVertical: 12,
    paddingHorizontal: 10,
    borderWidth: 1,
    borderColor: "gray",
    borderRadius: 4,
    color: "black",
    paddingRight: 30, // to ensure the text is never behind the icon
  },
  inputAndroid: {
    fontSize: 16,
    paddingHorizontal: 10,
    paddingVertical: 8,
    borderWidth: 0.5,
    borderColor: "purple",
    borderRadius: 8,
    color: "black",
    paddingRight: 30, // to ensure the text is never behind the icon
  },
});

const styles = StyleSheet.create({
  container: {
    flex: 1,
    width: width,
  },
  name: {
    fontSize: 20,
    padding: 20,
  },
  scrollContainer: {
    width: width,
    alignItems: "center",
    justifyContent: "center",
    alignSelf: "center",
  },
  card: {
    width: wp("90%"),
    alignSelf: "stretch",
    textAlign: "center",
    padding: 20,
    backgroundColor: "#ebebeb",
    marginTop: 10,
    justifyContent: "center",
  },
  btnOptions: {
    marginBottom: 5,
    padding: 10,
    textAlign: "center",
    alignItems: "center",
    justifyContent: "center",
    width: wp("30%"),
  },
  options: {
    flexDirection: "row",
    justifyContent: "space-between",
  },
  btn: {
    marginBottom: 10,
    padding: 15,
    textAlign: "center",
    alignItems: "center",
    justifyContent: "center",
    width: wp("90%"),
  },
  text: {
    fontSize: wp("4%"),
    marginVertical: 5,
  },
  image: {
    width: wp("90%"),
    height: hp("50%"),
  },

  // Modal
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 25,
  },
  modalView: {
    height: 500,
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2,
  },
  buttonOpen: {
    backgroundColor: "#F194FF",
  },
  buttonClose: {
    backgroundColor: "#2196F3",
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center",
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center",
  },
});
